export const DxzSpaceHomeJoined = ( props : props ) => {
	
	const { spaceInfo } = props;
	
	const reax_upload_banner = useRef( reaxel_upload_pics() );
	const {JoinedBtn} = reaxel_joined_Space_list();
	return <>
		<div
			className = { less.spaceInfo }
			style = { {
				background : "#f4f5f6" ,
				display : "flex" ,
				justifyContent : "center" ,
				flexFlow : "column wrap" ,
				minWidth : "fit-content" ,
				minHeight:"fit-content",
				paddingBottom:"64px",
			} }
		>
			<header
				className = { less.tabSettingNav }
			>
				<DxzTokenOverview />
				<SettingIcon />
			</header>
			<div
				className = { less.banner }
				style = { {
					position : "relative" ,
					width : "1200px" ,
					display : "flex" ,
					justifyContent : "center" ,
					borderRadius : "12px 12px 0px 0px" ,
				} }
			>
				<Img
					src = { spaceInfo?.bgUrl }
					width = "1200px"
					height = "300px"
					alt = ""
					style = { {
						borderRadius : "12px 12px 0px 0px" ,
						minWidth : "100%" ,
						height : "300px" ,
						objectFit:"cover",
						objectPosition:"50% 50%"
					} }
				/>
				<div
					style={{
						position : "absolute" ,
						top : "204px" ,
						left : "32px" ,
						backgroundColor : "#eee",
						borderRadius : "16px" ,
					}}
				>
					<Img
						src = { spaceInfo?.iconUrl }
						style = { {
							borderRadius : "16px" ,
							width : "116px" ,
							height : "116px" ,
							objectFit : "cover" ,
							objectPosition : "50% 50%" ,
							border:"4px solid #ffffff"
						} }
					/>
				</div>
				
				<div
					className = { less.editCover }
					onClick = { () => {
						reax_upload_banner.current.space_info_banner( spaceInfo.spaceID );
					} }
				>
					Edit cover
				</div>
			</div>
			<div
				className = "info-box"
				style = { {
					padding : "0 32px 34px 32px" ,
					width : "1200px" ,
					borderRadius : "0px 0px 12px 12px" ,
					backgroundColor : "#ffffff" ,
					display : "block" ,
				} }
			>
				<div
					className = { less.spaceJoin }
				>
					<span
						className = { less.spaceName }
					>{ spaceInfo?.name }
					</span>
					<div
						style = { {
							display : "flex" ,
							justifyContent:"space-between",
						} }
					>
						<ShareIcon></ShareIcon>
						<JoinedBtn
							spaceID = {spaceInfo?.spaceID}
							style = { {
								width : "140px" ,
								height : "48px" ,
								borderRadius : "12px" ,
								fontSize : "16px" ,
								fontWeight : "600" ,
								background:"#0070f3",
								color:"#ffffff",
							} }
						/>
					</div>
				</div>
				<div
					className = "sharing-box"
					style = { {
						display : "flex" ,
						alignItems : "center" ,
					} }
				>
					<WalletAddressCopyBox
						walletAddr = { spaceInfo?.addrChain }
					/>
					<div
						style = { {
							display : "flex" ,
							flexDirection : "row" ,
							alignItems : "flex-start" ,
						} }
					>
						<SocialShare />
					</div>
				</div>
				<p
					className = { less.bios }
				>
					{ spaceInfo?.bio }
				</p>
			</div>
			<div
				className = { less.contentBox }
			>
				<div
					className = { less.contentLeft }
				>
					<ContentListFirst></ContentListFirst>
					<ContentListSecond></ContentListSecond>
				</div>
				<div
					className = "content-right"
					style = { {
						width : "340px" ,
						backgroundColor : "#ffffff" ,
						borderRadius : "12px" ,
						padding : "24px 24px 24px 24px" ,
						display : "block" ,
						position : "relative" ,
					} }
				>
					<TitleEdit></TitleEdit>
					<div
						className = { less.memberLists }
						style = { {
							marginTop : "32px" ,
							display : "block" ,
							overflow : "auto" ,
							flex : "1" ,
							height : "775px" ,
							userSelect : "none" ,
							position : "absolute" ,
							right : '0' ,
							width : "316px" ,
						} }
					>
						{ new Array( 44 ).fill( '' ).map( ( v , i ) =>
							<MemberItem
								key = { i }
							></MemberItem> ,
						) }
					</div>
				</div>
			</div>
		</div>
	</>;
};


